"use strict";window.plankton=window.plankton||{},window.plankton.text={},window.plankton.text.capitalize=function(n){return n.replace(/(?:^|\s)\S/g,function(n){return n.toUpperCase()})},window.plankton.text.camelize=function(n){return n.replace(/(?:^\w|[A-Z]|\b\w)/g,function(n,t){return 0==t?n.toLowerCase():n.toUpperCase()}).replace(/\s+/g,"")},window.plankton.text.ucwords=function(n){return n=n.toLowerCase(),n.replace(/(^([a-zA-Z\p{M}]))|([ -][a-zA-Z\p{M}])/g,function(n){return n.toUpperCase()})};